mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rnum, rcp.* FROM ( SELECT r.rcp_no AS rcpNo, r.user_no AS userNo, r.rcp_name AS title, r.rcp_info AS info, NULL AS reportNo, NULL AS detail, NULL AS content, NULL AS reportedAt, 'RECIPE' AS type FROM RECIPE r JOIN ( SELECT ref_no FROM LIKES WHERE ref_type = 'RECIPE' AND like_status = 'LIKE' GROUP BY ref_no HAVING COUNT(*) > 5 ) l ON r.rcp_no = l.ref_no
UNION ALL
SELECT rep.ref_no AS rcpNo, rep.user_no AS userNo, NULL AS title, NULL AS info, rep.report_no AS reportNo, rt.detail AS detail, rep.content AS content, rep.reported_at AS reportedAt, 'REPORT' AS type FROM REPORT rep JOIN REPORT_TYPE rt USING (report_type) WHERE rep.resolved = 'N' AND rt.category = 'RECIPE' ) rcp ORDER BY CASE WHEN type = 'RECIPE' THEN 1 ELSE 2 END, rcpNo DESC ) WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}

mapper.update:
	UPDATE RECIPE
    SET approval = 'C'
    WHERE rcp_no = #{rcpNo}

	UPDATE RECIPE
    SET approval = 'Y'
    WHERE rcp_no = #{rcpNo}

	UPDATE CHALLENGE_FORM
    SET resolved = 'Y'
    WHERE form_no = #{formNo}

	UPDATE REPORT
    SET resolved = 'Y'
    WHERE report_no = #{reportNo}

mapper.select:
SELECT * FROM CHALLENGE_INFO
WHERE start_date <= #{endDate}
 	AND end_date >= #{startDate}

SELECT * FROM ANNOUNCEMENT
WHERE start_date <= #{endDate}
 	AND end_date >= #{startDate}

mapper.update:
UPDATE ANNOUNCEMENT
SET start_date = #{startDate},
	end_date = #{endDate},
	content = #{content}
WHERE ancmt_no = #{ancmtNo}

UPDATE CHALLENGE_INFO
SET title = #{title},
	start_date = #{startDate},
	end_date = #{endDate},
	image_no = #{imageNo}
WHERE ch_info_no = #{chInfoNo}

mapper.select:
SELECT * FROM CHALLENGE_INFO
WHERE start_date <= #{endDate}
 	AND end_date >= #{startDate}
 	AND ch_info_no != #{chInfoNo}

SELECT * FROM ( SELECT ROWNUM AS rnum, u.* FROM ( SELECT M.ing_no, I.ing_name, C.ing_code_name FROM ING_METHOD M JOIN INGREDIENT I ON M.ing_no = I.ing_no JOIN ING_CODE C ON I.ing_code = C.ing_code
UNION ALL
SELECT M.ing_no, I.ing_name, C.ing_code_name FROM ING_PAIR M JOIN INGREDIENT I ON M.ing_no = I.ing_no JOIN ING_CODE C ON I.ing_code = C.ing_code ORDER BY 1 ) u ) WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}
